import React, { useState, useEffect } from 'react';
import Map from '../components/Map';
import NearestMarkets from '../components/NearestMarkets';
import ProductList from '../components/ProductList';

const Home = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [nearestMarkets, setNearestMarkets] = useState([
    {
      name: 'Migros',
      address: 'Attar, SÖĞÜT Cd. No:8, 06420 Etimesgut/Ankara, Türkiye',
      distance: '22.6',
      duration: '27',
      position: { lat: 39.9334, lng: 32.8597 }
    },
    {
      name: 'A101',
      address: 'Aşıkpaşa, 1478. Cad. No:2/A D:19, 06000 Etimesgut/Ankara, Türkiye',
      distance: '25.7',
      duration: '23',
      position: { lat: 39.9234, lng: 32.8497 }
    },
    {
      name: 'Migros',
      address: 'Eryaman, 285. Sk. No: 5, 06824 Etimesgut/Ankara, Türkiye',
      distance: '22.2',
      duration: '25',
      position: { lat: 39.9434, lng: 32.8697 }
    },
    {
      name: 'A101',
      address: 'Selçuklu, Prof. Dr. Necmettin Erbakan Cd No:55, 06936 Sincan/Ankara, Türkiye',
      distance: '24.1',
      duration: '24',
      position: { lat: 39.9134, lng: 32.8397 }
    }
  ]);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (err) {
      setError('Ürünler yüklenirken bir hata oluştu');
    }
  };

  const scrapeProducts = async () => {
    setLoading(true);
    setError(null);
    try {
      await fetch('http://localhost:5000/api/scrape');
      await fetchProducts();
    } catch (err) {
      setError('Ürünler çekilirken bir hata oluştu');
    } finally {
      setLoading(false);
    }
  };

  const handleMarkerClick = (marker) => {
    console.log('Seçilen market:', marker);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="home">
      <h1>Ürün Fiyat Karşılaştırma</h1>

      <div className="search-container">
        <input
          type="text"
          placeholder="Ürün adı girin..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={() => {}}>Ara</button>
      </div>

      <div className="main-content">
        <div className="map-container">
          <Map 
            markers={nearestMarkets} 
            onMarkerClick={handleMarkerClick}
          />
        </div>

        <div className="markets-container">
          <NearestMarkets markets={nearestMarkets} />
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <button 
        className="update-button"
        onClick={scrapeProducts}
        disabled={loading}
      >
        {loading ? 'Ürünler Çekiliyor...' : 'Ürünleri Güncelle'}
      </button>

      <ProductList products={products.filter(product => 
        searchQuery ? product.name.toLowerCase().includes(searchQuery.toLowerCase()) : true
      )} />
    </div>
  );
};

export default Home;
